// filepath: teaching-platform/frontend/src/components/AdminDashboard.js
import React, { useState, useEffect } from "react";
import {
  getUsers,
  addUser,
  updateUser,
  deleteUser,
  generateCode,
} from "../api";
import { Link } from "react-router-dom"; // Import Link from react-router-dom

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({
    username: "",
    password: "",
    role: "student",
    code: "",
  });
  const [editingUser, setEditingUser] = useState(null);
  const [generatedCode, setGeneratedCode] = useState("");

  useEffect(() => {
    getUsers()
      .then(setUsers)
      .catch((err) => console.error(err));
  }, []);

  const handleAddUser = async () => {
    try {
      const user = await addUser(newUser);
      setUsers([...users, user]);
      setNewUser({ username: "", password: "", role: "student", code: "" });
    } catch (err) {
      console.error(err);
      alert(err.response.data.error);
    }
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
  };

  const handleUpdateUser = async () => {
    try {
      const updatedUser = await updateUser(editingUser);
      setUsers(
        users.map((user) => (user.id === updatedUser.id ? updatedUser : user))
      );
      setEditingUser(null);
    } catch (err) {
      console.error(err);
      alert(err.response.data.error);
    }
  };

  const handleDeleteUser = async (id) => {
    try {
      await deleteUser(id);
      setUsers(users.filter((user) => user.id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  const handleGenerateCode = async (role) => {
    try {
      const { code } = await generateCode(role);
      setGeneratedCode(code);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <Link to="/add-admin">Add New Admin</Link>{" "}
      {/* Add link to AddAdmin page */}
      <input
        type="text"
        placeholder="Username"
        value={newUser.username}
        onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
      />
      <input
        type="password"
        placeholder="Password"
        value={newUser.password}
        onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
      />
      <input
        type="text"
        placeholder="Code"
        value={newUser.code}
        onChange={(e) => setNewUser({ ...newUser, code: e.target.value })}
      />
      <select
        value={newUser.role}
        onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
      >
        <option value="student">Student</option>
        <option value="teacher">Teacher</option>
        <option value="admin">Admin</option>
      </select>
      <button onClick={handleAddUser}>Add User</button>
      {editingUser && (
        <div>
          <input
            type="text"
            placeholder="Username"
            value={editingUser.username}
            onChange={(e) =>
              setEditingUser({ ...editingUser, username: e.target.value })
            }
          />
          <select
            value={editingUser.role}
            onChange={(e) =>
              setEditingUser({ ...editingUser, role: e.target.value })
            }
          >
            <option value="student">Student</option>
            <option value="teacher">Teacher</option>
            <option value="admin">Admin</option>
          </select>
          <button onClick={handleUpdateUser}>Update User</button>
        </div>
      )}
      <ul>
        {users.map((user) => (
          <li key={user.id}>
            {user.username} ({user.role})
            <button onClick={() => handleEditUser(user)}>Edit</button>
            <button onClick={() => handleDeleteUser(user.id)}>Delete</button>
          </li>
        ))}
      </ul>
      <div>
        <h2>Generate Code</h2>
        <select onChange={(e) => handleGenerateCode(e.target.value)}>
          <option value="student">Student</option>
          <option value="teacher">Teacher</option>
          <option value="admin">Admin</option>
        </select>
        {generatedCode && <p>Generated Code: {generatedCode}</p>}
      </div>
    </div>
  );
};

export default AdminDashboard;
