import React, { useState, useEffect } from "react";
import {
  getUsers,
  deleteUser,
  addUser,
  generateCode,
  getCodes,
  updateUser,
} from "../../api"; // Ensure getCodes and updateUser are imported
import { Container, Table, Button, Form, Modal, Alert } from "react-bootstrap";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [codes, setCodes] = useState([]);
  const [showAddUserModal, setShowAddUserModal] = useState(false);
  const [showGenerateCodeModal, setShowGenerateCodeModal] = useState(false);
  const [showEditUserModal, setShowEditUserModal] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("student");
  const [code, setCode] = useState("");
  const [error, setError] = useState("");
  const [generatedCode, setGeneratedCode] = useState("");
  const [editingUser, setEditingUser] = useState(null);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const data = await getUsers();
        setUsers(data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchUsers();
  }, []);

  useEffect(() => {
    const fetchCodes = async () => {
      try {
        const data = await getCodes();
        setCodes(data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchCodes();
  }, []);

  const handleDelete = async (userId) => {
    try {
      await deleteUser(userId);
      setUsers(users.filter((user) => user.id !== userId));
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddUser = async (e) => {
    e.preventDefault();
    try {
      const newUser = { username, password, role, code };
      await addUser(newUser);
      alert("User added successfully!");
      setShowAddUserModal(false);
      setUsername("");
      setPassword("");
      setRole("student");
      setCode("");
      const data = await getUsers();
      setUsers(data);
    } catch (err) {
      console.error(err);
      setError("Failed to add user");
    }
  };

  const handleGenerateCode = async (e) => {
    e.preventDefault();
    try {
      const data = await generateCode(role);
      setGeneratedCode(data.code);
      setShowGenerateCodeModal(false);
      const codesData = await getCodes();
      setCodes(codesData);
    } catch (err) {
      console.error(err);
      setError("Failed to generate code");
    }
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
    setUsername(user.username);
    setRole(user.role);
    setShowEditUserModal(true);
  };

  const handleUpdateUser = async (e) => {
    e.preventDefault();
    try {
      await updateUser({ id: editingUser.id, username, role });
      alert("User updated successfully!");
      setShowEditUserModal(false);
      setEditingUser(null);
      setUsername("");
      setRole("student");
      const data = await getUsers();
      setUsers(data);
    } catch (err) {
      console.error(err);
      setError("Failed to update user");
    }
  };

  return (
    <Container className="mt-5">
      <h1>Admin Dashboard</h1>
      <Button variant="primary" onClick={() => setShowAddUserModal(true)}>
        Add User
      </Button>
      <Button
        variant="secondary"
        onClick={() => setShowGenerateCodeModal(true)}
        className="ml-2"
      >
        Generate Code
      </Button>
      <Table striped bordered hover className="mt-3">
        <thead>
          <tr>
            <th>ID</th>
            <th>Username</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.username}</td>
              <td>{user.role}</td>
              <td>
                <Button variant="danger" onClick={() => handleDelete(user.id)}>
                  Delete
                </Button>
                <Button
                  variant="warning"
                  onClick={() => handleEditUser(user)}
                  className="ml-2"
                >
                  Edit
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      <h2 className="mt-5">Generated Codes</h2>
      <Table striped bordered hover className="mt-3">
        <thead>
          <tr>
            <th>ID</th>
            <th>Code</th>
            <th>Role</th>
            <th>Used</th>
          </tr>
        </thead>
        <tbody>
          {codes.map((code) => (
            <tr key={code.id}>
              <td>{code.id}</td>
              <td>{code.code}</td>
              <td>{code.role}</td>
              <td>{code.used ? "Yes" : "No"}</td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Add User Modal */}
      <Modal show={showAddUserModal} onHide={() => setShowAddUserModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add User</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleAddUser}>
            <Form.Group controlId="formUsername">
              <Form.Label>Username</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="formPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="formRole">
              <Form.Label>Role</Form.Label>
              <Form.Control
                as="select"
                value={role}
                onChange={(e) => setRole(e.target.value)}
              >
                <option value="student">Student</option>
                <option value="teacher">Teacher</option>
                <option value="admin">Admin</option>
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="formCode">
              <Form.Label>Code</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter code (if applicable)"
                value={code}
                onChange={(e) => setCode(e.target.value)}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Add User
            </Button>
          </Form>
        </Modal.Body>
      </Modal>

      {/* Generate Code Modal */}
      <Modal
        show={showGenerateCodeModal}
        onHide={() => setShowGenerateCodeModal(false)}
      >
        <Modal.Header closeButton>
          <Modal.Title>Generate Code</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleGenerateCode}>
            <Form.Group controlId="formRole">
              <Form.Label>Role</Form.Label>
              <Form.Control
                as="select"
                value={role}
                onChange={(e) => setRole(e.target.value)}
              >
                <option value="student">Student</option>
                <option value="teacher">Teacher</option>
                <option value="admin">Admin</option>
              </Form.Control>
            </Form.Group>
            <Button variant="primary" type="submit">
              Generate Code
            </Button>
          </Form>
          {generatedCode && (
            <Alert variant="success" className="mt-3">
              Generated Code: {generatedCode}
            </Alert>
          )}
        </Modal.Body>
      </Modal>

      {/* Edit User Modal */}
      <Modal
        show={showEditUserModal}
        onHide={() => setShowEditUserModal(false)}
      >
        <Modal.Header closeButton>
          <Modal.Title>Edit User</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleUpdateUser}>
            <Form.Group controlId="formUsername">
              <Form.Label>Username</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="formRole">
              <Form.Label>Role</Form.Label>
              <Form.Control
                as="select"
                value={role}
                onChange={(e) => setRole(e.target.value)}
              >
                <option value="student">Student</option>
                <option value="teacher">Teacher</option>
                <option value="admin">Admin</option>
              </Form.Control>
            </Form.Group>
            <Button variant="primary" type="submit">
              Update User
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default AdminDashboard;
