import React, { useState, useEffect } from "react";
import {
  getUsers,
  deleteUser,
  addUser,
  generateCode,
  getCodes,
  updateUser,
  deleteCode,
  getPosts,
  updatePost,
  deletePost,
} from "../../api";
import {
  Container,
  Table,
  Button,
  Form,
  Alert,
  Row,
  Col,
  Modal,
} from "react-bootstrap";
import ModalForm from "../common/ModalForm";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [codes, setCodes] = useState([]);
  const [posts, setPosts] = useState([]);
  const [showAddUserModal, setShowAddUserModal] = useState(false);
  const [showGenerateCodeModal, setShowGenerateCodeModal] = useState(false);
  const [showEditUserModal, setShowEditUserModal] = useState(false);
  const [showEditPostModal, setShowEditPostModal] = useState(false);
  const [showUsersTable, setShowUsersTable] = useState(true);
  const [showCodesTable, setShowCodesTable] = useState(true);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("student");
  const [code, setCode] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [error, setError] = useState("");
  const [generatedCodes, setGeneratedCodes] = useState([]);
  const [editingUser, setEditingUser] = useState(null);
  const [editingPost, setEditingPost] = useState(null);
  const [postTitle, setPostTitle] = useState("");
  const [postContent, setPostContent] = useState("");
  const [postType, setPostType] = useState("video");

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const data = await getUsers();
        setUsers(data);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch users");
      }
    };
    fetchUsers();
  }, []);

  useEffect(() => {
    const fetchCodes = async () => {
      try {
        const data = await getCodes();
        setCodes(data);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch codes");
      }
    };
    fetchCodes();
  }, []);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const data = await getPosts();
        setPosts(data);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch posts");
      }
    };
    fetchPosts();
  }, []);

  const handleDeleteUser = async (userId) => {
    try {
      await deleteUser(userId);
      setUsers(users.filter((user) => user.id !== userId));
    } catch (err) {
      console.error(err);
      setError("Failed to delete user");
    }
  };

  const handleDeleteCode = async (codeId) => {
    try {
      await deleteCode(codeId);
      setCodes(codes.filter((code) => code.id !== codeId));
    } catch (err) {
      console.error(err);
      setError("Failed to delete code");
    }
  };

  const handleDeletePost = async (postId) => {
    try {
      await deletePost(postId);
      setPosts(posts.filter((post) => post.id !== postId));
    } catch (err) {
      console.error(err);
      setError("Failed to delete post");
    }
  };

  const handleAddUser = async (e) => {
    e.preventDefault();
    try {
      const newUser = { username, password, role, code };
      await addUser(newUser);
      alert("User added successfully!");
      setShowAddUserModal(false);
      setUsername("");
      setPassword("");
      setRole("student");
      setCode("");
      const data = await getUsers();
      setUsers(data);
    } catch (err) {
      console.error(err);
      setError("Failed to add user");
    }
  };

  const handleGenerateCode = async (e) => {
    e.preventDefault();
    try {
      const data = await generateCode(role, quantity);
      setGeneratedCodes(data.codes);
      setShowGenerateCodeModal(false);
      const codesData = await getCodes();
      setCodes(codesData);
    } catch (err) {
      console.error(err);
      setError("Failed to generate codes");
    }
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
    setUsername(user.username);
    setRole(user.role);
    setShowEditUserModal(true);
  };

  const handleUpdateUser = async (e) => {
    e.preventDefault();
    try {
      await updateUser({ id: editingUser.id, username, role });
      alert("User updated successfully!");
      setShowEditUserModal(false);
      setEditingUser(null);
      setUsername("");
      setRole("student");
      const data = await getUsers();
      setUsers(data);
    } catch (err) {
      console.error(err);
      setError("Failed to update user");
    }
  };

  const handleEditPost = (post) => {
    setEditingPost(post);
    setPostTitle(post.title);
    setPostContent(post.content);
    setPostType(post.type);
    setShowEditPostModal(true);
  };

  const handleUpdatePost = async (e) => {
    e.preventDefault();
    try {
      await updatePost({
        id: editingPost.id,
        title: postTitle,
        content: postContent,
        type: postType,
      });
      alert("Post updated successfully!");
      setShowEditPostModal(false);
      setEditingPost(null);
      setPostTitle("");
      setPostContent("");
      setPostType("video");
      const data = await getPosts();
      setPosts(data);
    } catch (err) {
      console.error(err);
      setError("Failed to update post");
    }
  };

  const studentCodes = codes.filter((code) => code.role === "student");
  const teacherCodes = codes.filter((code) => code.role === "teacher");

  return (
    <Container className="mt-5">
      <h1>Admin Dashboard</h1>
      {error && <Alert variant="danger">{error}</Alert>}
      <Button variant="primary" onClick={() => setShowAddUserModal(true)}>
        Add User
      </Button>
      <Button
        variant="secondary"
        onClick={() => setShowGenerateCodeModal(true)}
        className="ml-2"
      >
        Generate Code
      </Button>
      <Button
        variant="info"
        onClick={() => setShowUsersTable(!showUsersTable)}
        className="ml-2"
      >
        {showUsersTable ? "Hide Users Table" : "Show Users Table"}
      </Button>
      <Button
        variant="info"
        onClick={() => setShowCodesTable(!showCodesTable)}
        className="ml-2"
      >
        {showCodesTable ? "Hide Codes Table" : "Show Codes Table"}
      </Button>

      {showUsersTable && (
        <Table striped bordered hover className="mt-3">
          <thead>
            <tr>
              <th>ID</th>
              <th>Username</th>
              <th>Role</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.username}</td>
                <td>{user.role}</td>
                <td>
                  <Button
                    variant="danger"
                    onClick={() => handleDeleteUser(user.id)}
                  >
                    Delete
                  </Button>
                  <Button
                    variant="warning"
                    onClick={() => handleEditUser(user)}
                    className="ml-2"
                  >
                    Edit
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}

      {showCodesTable && (
        <Row className="mt-5">
          <Col>
            <h3>Student Codes</h3>
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Code</th>
                  <th>Role</th>
                  <th>Used</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {studentCodes.map((code) => (
                  <tr key={code.id}>
                    <td>{code.id}</td>
                    <td>{code.code}</td>
                    <td>{code.role}</td>
                    <td>{code.used ? "Yes" : "No"}</td>
                    <td>
                      <Button
                        variant="danger"
                        onClick={() => handleDeleteCode(code.id)}
                      >
                        Delete
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </Col>
          <Col>
            <h3>Teacher Codes</h3>
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Code</th>
                  <th>Role</th>
                  <th>Used</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {teacherCodes.map((code) => (
                  <tr key={code.id}>
                    <td>{code.id}</td>
                    <td>{code.code}</td>
                    <td>{code.role}</td>
                    <td>{code.used ? "Yes" : "No"}</td>
                    <td>
                      <Button
                        variant="danger"
                        onClick={() => handleDeleteCode(code.id)}
                      >
                        Delete
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </Col>
        </Row>
      )}

      <h2 className="mt-5">Posts</h2>
      <Table striped bordered hover className="mt-3">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Content</th>
            <th>Type</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {posts.map((post) => (
            <tr key={post.id}>
              <td>{post.id}</td>
              <td>{post.title}</td>
              <td>{post.content}</td>
              <td>{post.type}</td>
              <td>
                <Button variant="warning" onClick={() => handleEditPost(post)}>
                  Edit
                </Button>
                <Button
                  variant="danger"
                  onClick={() => handleDeletePost(post.id)}
                  className="ml-2"
                >
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Add User Modal */}
      <ModalForm
        show={showAddUserModal}
        onHide={() => setShowAddUserModal(false)}
        title="Add User"
        error={error}
        onSubmit={handleAddUser}
        submitButtonText="Add User"
      >
        <Form.Group controlId="formUsername">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="formPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="formRole">
          <Form.Label>Role</Form.Label>
          <Form.Control
            as="select"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          >
            <option value="student">Student</option>
            <option value="teacher">Teacher</option>
            <option value="admin">Admin</option>
          </Form.Control>
        </Form.Group>
        <Form.Group controlId="formCode">
          <Form.Label>Code</Form.Label>
          <Form.Control
            as="select"
            value={code}
            onChange={(e) => setCode(e.target.value)}
          >
            <option value="">Select Code</option>
            {codes.map((code) => (
              <option key={code.id} value={code.code}>
                {code.code}
              </option>
            ))}
          </Form.Control>
        </Form.Group>
      </ModalForm>

      {/* Generate Code Modal */}
      <ModalForm
        show={showGenerateCodeModal}
        onHide={() => setShowGenerateCodeModal(false)}
        title="Generate Code"
        error={error}
        onSubmit={handleGenerateCode}
        submitButtonText="Generate Codes"
      >
        <Form.Group controlId="formRole">
          <Form.Label>Role</Form.Label>
          <Form.Control
            as="select"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          >
            <option value="student">Student</option>
            <option value="teacher">Teacher</option>
            <option value="admin">Admin</option>
          </Form.Control>
        </Form.Group>
        <Form.Group controlId="formQuantity">
          <Form.Label>Quantity</Form.Label>
          <Form.Control
            type="number"
            min="1"
            max="100"
            value={quantity}
            onChange={(e) => setQuantity(parseInt(e.target.value))}
          />
        </Form.Group>
        {generatedCodes.length > 0 && (
          <Alert variant="success" className="mt-3">
            Generated Codes: {generatedCodes.join(", ")}
          </Alert>
        )}
      </ModalForm>

      {/* Edit User Modal */}
      <ModalForm
        show={showEditUserModal}
        onHide={() => setShowEditUserModal(false)}
        title="Edit User"
        error={error}
        onSubmit={handleUpdateUser}
        submitButtonText="Update User"
      >
        <Form.Group controlId="formUsername">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="formRole">
          <Form.Label>Role</Form.Label>
          <Form.Control
            as="select"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          >
            <option value="student">Student</option>
            <option value="teacher">Teacher</option>
            <option value="admin">Admin</option>
          </Form.Control>
        </Form.Group>
      </ModalForm>

      {/* Edit Post Modal */}
      <Modal
        show={showEditPostModal}
        onHide={() => setShowEditPostModal(false)}
      >
        <Modal.Header closeButton>
          <Modal.Title>Edit Post</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleUpdatePost}>
            <Form.Group controlId="formPostTitle">
              <Form.Label>Title</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter title"
                value={postTitle}
                onChange={(e) => setPostTitle(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="formPostContent">
              <Form.Label>Content</Form.Label>
              <Form.Control
                as="textarea"
                placeholder="Enter content"
                value={postContent}
                onChange={(e) => setPostContent(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="formPostType">
              <Form.Label>Type</Form.Label>
              <Form.Control
                as="select"
                value={postType}
                onChange={(e) => setPostType(e.target.value)}
              >
                <option value="video">Video</option>
                <option value="image">Image</option>
                <option value="file">File</option>
              </Form.Control>
            </Form.Group>
            <Button variant="primary" type="submit" className="mt-3">
              Update Post
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default AdminDashboard;
