import axios from "axios";

const API_URL = "http://localhost:3000/api";

// Helper function to get the token from localStorage
const getAuthHeaders = () => {
  const token = localStorage.getItem("token");
  return {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };
};

export const login = async (fullName, password, code) => {
  const response = await axios.post(`${API_URL}/login`, {
    fullName,
    password,
    code,
  });
  return response.data;
};

export const getUsers = async () => {
  const response = await axios.get(`${API_URL}/users`, getAuthHeaders());
  return response.data;
};

export const addUser = async (user) => {
  const response = await axios.post(`${API_URL}/users`, user, getAuthHeaders());
  return response.data;
};

export const updateUser = async (user) => {
  const response = await axios.put(`${API_URL}/users`, user, getAuthHeaders());
  return response.data;
};

export const deleteUser = async (id) => {
  const response = await axios.delete(
    `${API_URL}/users/${id}`,
    getAuthHeaders()
  );
  return response.data;
};

export const generateCode = async (role, quantity) => {
  const response = await axios.post(
    `${API_URL}/generate-code`,
    { role, quantity },
    getAuthHeaders()
  );
  return response.data;
};

export const getCodes = async () => {
  const response = await axios.get(`${API_URL}/codes`, getAuthHeaders());
  return response.data;
};

export const deleteCode = async (id) => {
  const response = await axios.delete(
    `${API_URL}/codes/${id}`,
    getAuthHeaders()
  );
  return response.data;
};

// Post API calls
export const addPost = async (post) => {
  const response = await axios.post(`${API_URL}/posts`, post, getAuthHeaders());
  return response.data;
};

export const getPosts = async () => {
  const response = await axios.get(`${API_URL}/posts`, getAuthHeaders());
  return response.data;
};

export const updatePost = async (post) => {
  const response = await axios.put(`${API_URL}/posts`, post, getAuthHeaders());
  return response.data;
};

export const deletePost = async (id) => {
  const response = await axios.delete(
    `${API_URL}/posts/${id}`,
    getAuthHeaders()
  );
  return response.data;
};

// Register user
export const registerUser = async (user) => {
  const response = await axios.post(`${API_URL}/register`, user);
  return response.data;
};
