const getPosts = async () => {
  try {
    const result = await pool.query(
      "SELECT * FROM posts ORDER BY created_at DESC"
    );
    return result.rows.map((post) => ({
      ...post,
      file_data: post.file_data ? post.file_data.toString("base64") : null, // Convert file_data to base64 string if not null
    }));
  } catch (err) {
    console.error(err);
    throw err;
  }
};
