import React, { useState, useEffect } from "react";
import { getPosts } from "../api";

const StudentDashboard = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const data = await getPosts();
        setPosts(data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchPosts();
  }, []);

  return (
    <div>
      <h1>Student Dashboard</h1>
      <ul>
        {posts.map((post) => (
          <li key={post.id}>
            <h2>{post.title}</h2>
            {post.type === "video" && (
              <video
                src={`data:${post.file_mime};base64,${post.file_data.toString(
                  "base64"
                )}`}
                controls
              />
            )}
            {post.type === "image" && (
              <img
                src={`data:${post.file_mime};base64,${post.file_data.toString(
                  "base64"
                )}`}
                alt={post.title}
              />
            )}
            {post.type === "file" && (
              <a
                href={`data:${post.file_mime};base64,${post.file_data.toString(
                  "base64"
                )}`}
                download={post.file_name}
              >
                {post.title}
              </a>
            )}
            <p>Posted by: {post.created_by}</p>
            <p>Posted at: {post.created_at}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StudentDashboard;
